/* Validate a device handle */
static inline BOOL IsValidDevice(PWDC_DEVICE pDev, const CHAR *sFunc)
{
    if (!pDev || !(PNEVISPCI_DEV_CTX)(pDev->pCtx))
    {
        snprintf(gsNEVISPCI_LastErr, sizeof(gsNEVISPCI_LastErr) - 1,
            "%s: NULL device %s\n", sFunc, !pDev ? "handle" : "context");
        ErrLog(gsNEVISPCI_LastErr);
        return FALSE;
    }

    return TRUE;
}

