/* Log a debug error message */
static void ErrLog(const CHAR *sFormat, ...)
{
    va_list argp;

    va_start(argp, sFormat);
    vsnprintf(gsNEVISPCI_LastErr, sizeof(gsNEVISPCI_LastErr) - 1, sFormat, argp);
#ifdef DEBUG
        WDC_Err("NEVISPCI lib: %s", gsNEVISPCI_LastErr);
#endif
    va_end(argp);
}

/* Log a debug trace message */
static void TraceLog(const CHAR *sFormat, ...)
{
#ifdef DEBUG
    CHAR sMsg[256];
    va_list argp;

    va_start(argp, sFormat);
    vsnprintf(sMsg, sizeof(sMsg) - 1, sFormat, argp);
    WDC_Trace("NEVISPCI lib: %s", sMsg);
    va_end(argp);
#endif
}

/* Get last error */
const char *NEVISPCI_GetLastErr(void)
{
    return gsNEVISPCI_LastErr;
}

